INSERT INTO `wp_sfnctposts` VALUES (73, 1, '2014-06-24 22:37:54', '2014-06-24 22:37:54', '<center></center><a href="http://www.sfnct.org/wp-content/uploads/2014/06/members.jpg"><img class="alignnone size-medium wp-image-109" src="http://www.sfnct.org/wp-content/uploads/2014/06/members-300x201.jpg" alt="members" width="300" height="201" /></a>\r\n\r\n<strong>Our Members</strong>\r\n<ul>\r\n	<li>Booker T. Washington Community Service Center</li>\r\n</ul>\r\n<ul>\r\n	<li>Donaldina Cameron House</li>\r\n</ul>\r\n<ul>\r\n	<li>Good Samaritan Family Resource Center</li>\r\n</ul>\r\n<ul>\r\n	<li>I.T. Bookman Center</li>\r\n</ul>\r\n<ul>\r\n	<li>Mission Neighborhood Centers</li>\r\n</ul>\r\n<ul>\r\n	<li>Potrero Hill Neighborhood House</li>\r\n</ul>\r\n<ul>\r\n	<li><i>Richmond District Neighborhood Center*</i></li>\r\n</ul>\r\n<ul>\r\n	<li>Telegraph Hill Neighborhood Center</li>\r\n</ul>\r\n* <i>fiscal sponsor for SFNCT</i>', 'Members', '', 'publish', 'closed', 'closed', '', 'members', '', '', '2014-07-21 20:41:15', '2014-07-21 20:41:15', '', 0, 'http://www.sfnct.org/?page_id=73', 0, 'page', '', 0);
INSERT INTO `wp_sfnctposts` VALUES (75, 1, '2014-06-24 22:37:54', '2014-06-24 22:37:54', '&nbsp;\r\n\r\n<center></center><b>Our Members\r\n</b>\r\n<ul>\r\n	<li><b>Bernal Heights Neighborhood Center</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Booker T. Washington Community Service Center</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Donaldina Cameron House\r\n</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Good Samaritan Family Resource Center</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>I.T. Bookman Center</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Mission Neighborhood Centers</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Potrero Hill Neighborhood House</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b><i>Richmond District Neighborhood Center*</i>\r\n</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Telegraph Hill Neighborhood Center</b></li>\r\n</ul>\r\n* <i>fiscal sponsor for SFNCT</i>\r\n\r\n<img src="http://sfnct.org/mediac/400_0/media/9e65a1571822b82dffff8772ffffe41e.jpg" alt="" width="400" height="267" align="left" border="0" />\r\nSFNCT believes in a neighborhood-based, multi-service approach to improving the lives of City residents and the neighborhoods in which they live.\r\n\r\nOur members serve 30,000 people annually through programs, services, classes, community building, and community organizing initiatives, and hundreds of thousands of people annually through arts and cultural events. Neighborhood Centers identify and reinforce the strengths of individuals, families and communities. SFNCT members creatively and effectively address critical community needs in San Franciscos diverse neighborhoods.\r\n\r\nNeighborhood Centers are multi-cultural, multi-generational, place-based organizations where everyone is welcome. They provide a safe space for dialogue and debate so people can come together to solve issues impacting their community. Our members own or control their facilities, giving them permanence and deep roots in their neighborhoods.<b>\r\n</b>\r\nSFNCT''s members are rooted in the history and values of the Settlement House movement. Settlement Houses originated in the late 1800s with the goal of promoting social and economic equity for new immigrants through education, social services, community organizing, and community building.', '', '', 'inherit', 'closed', 'closed', '', '73-revision-v1', '', '', '2014-06-24 22:37:54', '2014-06-24 22:37:54', '', 73, 'http://www.sfnct.org/?p=75', 0, 'revision', '', 0);
INSERT INTO `wp_sfnctposts` VALUES (76, 1, '2014-06-24 22:38:16', '2014-06-24 22:38:16', '<center></center><b>Our Members\r\n</b>\r\n<ul>\r\n	<li><b>Bernal Heights Neighborhood Center</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Booker T. Washington Community Service Center</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Donaldina Cameron House\r\n</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Good Samaritan Family Resource Center</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>I.T. Bookman Center</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Mission Neighborhood Centers</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Potrero Hill Neighborhood House</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b><i>Richmond District Neighborhood Center*</i>\r\n</b></li>\r\n</ul>\r\n<ul>\r\n	<li><b>Telegraph Hill Neighborhood Center</b></li>\r\n</ul>\r\n* <i>fiscal sponsor for SFNCT</i>\r\n\r\n<img src="http://sfnct.org/mediac/400_0/media/9e65a1571822b82dffff8772ffffe41e.jpg" alt="" width="400" height="267" align="left" border="0" />\r\nSFNCT believes in a neighborhood-based, multi-service approach to improving the lives of City residents and the neighborhoods in which they live.\r\n\r\nOur members serve 30,000 people annually through programs, services, classes, community building, and community organizing initiatives, and hundreds of thousands of people annually through arts and cultural events. Neighborhood Centers identify and reinforce the strengths of individuals, families and communities. SFNCT members creatively and effectively address critical community needs in San Franciscos diverse neighborhoods.\r\n\r\nNeighborhood Centers are multi-cultural, multi-generational, place-based organizations where everyone is welcome. They provide a safe space for dialogue and debate so people can come together to solve issues impacting their community. Our members own or control their facilities, giving them permanence and deep roots in their neighborhoods.<b>\r\n</b>\r\nSFNCT''s members are rooted in the history and values of the Settlement House movement. Settlement Houses originated in the late 1800s with the goal of promoting social and economic equity for new immigrants through education, social services, community organizing, and community building.', '', '', 'inherit', 'closed', 'closed', '', '73-revision-v1', '', '', '2014-06-24 22:38:16', '2014-06-24 22:38:16', '', 73, 'http://www.sfnct.org/?p=76', 0, 'revision', '', 0);
