INSERT INTO `wp_sfnctposts` VALUES (78, 1, '2014-06-24 22:40:03', '2014-06-24 22:40:03', '<table border="0" width="611" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td width="20"></td>\r\n<td valign="top" bgcolor="#FFFFFF" width="571"><center></center><img src="http://sfnct.org/img/content/5701_5705.gif" alt="" />\r\n\r\n<img src="http://sfnct.org/mediac/400_0/media/DIR_104/9e65a1571822b82dffff86a0ffffe41e.jpg" alt="" width="400" height="265" align="left" border="0" />\r\n\r\n<b>San Francisco Neighborhood Centers Together (SFNCT) is a network for the City''s Neighborhood Centers. </b>We work with our members to build capacity through peer support, coaching, and training. We share resources, information, and best practices with our members, stakeholders, local government, and funders.\r\n<b>\r\n</b> Neighborhood Centers are anchor institutions that provide programs and services, and build connections between residents, community and neighborhood groups, schools, faith-based groups, merchants, and local public agencies.<b>\r\n</b>\r\n\r\n<img src="http://sfnct.org/mediac/400_0/media/18a690e951f510f3ffff827affffe415.jpg" alt="" width="400" height="267" align="left" border="0" />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<center></center><center></center></td>\r\n<td width="20"></td>\r\n</tr>\r\n</tbody>\r\n</table>', '', '', 'inherit', 'closed', 'closed', '', '53-revision-v1', '', '', '2014-06-24 22:40:03', '2014-06-24 22:40:03', '', 53, 'http://www.sfnct.org/?p=78', 0, 'revision', '', 0);
